customPhraseDescTS:
// 根据属性名称值编写自定义短语描述，如："我的短语是"+attrs.xxx
// 使用下拉框组件的变量attrs.xxx有对应的attrs.xxxItems的属性以便获得标签名集合
// 获取自定义模块的数据名称的函数：getModuleDataName(moduleID:number,dataID:number):string;
// 获取各种列表内数据名称的函数：getTypeDataListName(typeID: number, dataID: number):string;
// 获取自定义游戏数值描述的函数：getCustomGameNumber(customGameNumber: any[]):string;
// 获取自定义游戏字符串描述的函数：getCustomGameString(customGameString: any[]):string;
// 获取自定义游戏开关描述的函数：：getCustomGameSwitch(customGameSwitch: any[]):string;
// -- typeID ：0-全局变量 1-全局开关 2-全局字符串 3-玩家变量 4-玩家开关 5-玩家字符串
//           ：6-场景列表 7-场景对象原型列表 8-图块列表 9-行走图列表 10-行走图动作列表 11-立绘列表 12-立绘表情列表
//           ：13-事件库列表 14-对话框列表 15-动画列表 16-动画信号列表 17-界面列表 18-数据结构列表 19-自定义模块列表
//           ：20-自定义指令列表 21-自定义行为列表 22-自动元件列表 23-行走图部件列表 24-立绘部件列表 25-材质列表 26-场景对象模块
let str = '';
let yy = JudgeTypeConstantVariable(attrs.timeNum_y, attrs.timeNumVar_y, attrs.timeNumType_y)
let mm = JudgeTypeConstantVariable(attrs.timeNum_m, attrs.timeNumVar_m, attrs.timeNumType_m)
let dd = JudgeTypeConstantVariable(attrs.timeNum_d, attrs.timeNumVar_d, attrs.timeNumType_d)
let hh = JudgeTypeConstantVariable(attrs.timeNum_h, attrs.timeNumVar_h, attrs.timeNumType_h)
let ii = JudgeTypeConstantVariable(attrs.timeNum_i, attrs.timeNumVar_i, attrs.timeNumType_i)
let ss = JudgeTypeConstantVariable(attrs.timeNum_s, attrs.timeNumVar_s, attrs.timeNumType_s)
if (attrs.timeDataTypeList == 0) {
    str += "根据本地时间开始"
}
if (attrs.timeDataTypeList == 1) {
    str += "根据服务器时间开始"
}
if (attrs.timeDataTypeList == 2) {
    str += `根据 ${yy}/${mm}/${dd} ${hh}:${ii}:${ss} 开始`
}
str += ` => ${JudgeTypeConstantVariable(attrs.setTime, attrs.setTime, 1, 5)}`

if (attrs.timeGetStatus) {
    str += `  状态开关[${attrs.timeGetStatus}-${getTypeDataListName(4, attrs.timeGetStatus)}]`
}

if (attrs.setTimeStamp) {
    str += `  时间戳[${attrs.setTimeStamp}-${getTypeDataListName(3, attrs.setTimeStamp)}]`
}

str += ""
/**
 * 判断常量变量类型
 * @param {any} constant 常量
 * @param {number} variable 变量
 * @param {number} index_type 选项类型 0 = 常量 1 = 变量
 * @param {number} variable_type 变量类型
 */
function JudgeTypeConstantVariable(constant: any, variable: number, index_type: number, variable_type: number = 3, text_type: number = 0): any {
    let variable_value;
    if (index_type == 0) {
        variable_value = constant;
    } else {
        if (text_type == 0) {
            variable_value = `[${variable}-${getTypeDataListName(variable_type, variable)}]`
        } else {
            variable_value = `${variable}-${getTypeDataListName(variable_type, variable)}`
        }
    }
    return variable_value;
}

customPhraseDescJS:
var str = '';
var yy = JudgeTypeConstantVariable(attrs.timeNum_y, attrs.timeNumVar_y, attrs.timeNumType_y);
var mm = JudgeTypeConstantVariable(attrs.timeNum_m, attrs.timeNumVar_m, attrs.timeNumType_m);
var dd = JudgeTypeConstantVariable(attrs.timeNum_d, attrs.timeNumVar_d, attrs.timeNumType_d);
var hh = JudgeTypeConstantVariable(attrs.timeNum_h, attrs.timeNumVar_h, attrs.timeNumType_h);
var ii = JudgeTypeConstantVariable(attrs.timeNum_i, attrs.timeNumVar_i, attrs.timeNumType_i);
var ss = JudgeTypeConstantVariable(attrs.timeNum_s, attrs.timeNumVar_s, attrs.timeNumType_s);
if (attrs.timeDataTypeList == 0) {
    str += "根据本地时间开始";
}
if (attrs.timeDataTypeList == 1) {
    str += "根据服务器时间开始";
}
if (attrs.timeDataTypeList == 2) {
    str += "\u6839\u636E " + yy + "/" + mm + "/" + dd + " " + hh + ":" + ii + ":" + ss + " \u5F00\u59CB";
}
str += " => " + JudgeTypeConstantVariable(attrs.setTime, attrs.setTime, 1, 5);
if (attrs.timeGetStatus) {
    str += "  \u72B6\u6001\u5F00\u5173[" + attrs.timeGetStatus + "-" + getTypeDataListName(4, attrs.timeGetStatus) + "]";
}
if (attrs.setTimeStamp) {
    str += "  \u65F6\u95F4\u6233[" + attrs.setTimeStamp + "-" + getTypeDataListName(3, attrs.setTimeStamp) + "]";
}
str += "";
function JudgeTypeConstantVariable(constant, variable, index_type, variable_type, text_type) {
    if (variable_type === void 0) { variable_type = 3; }
    if (text_type === void 0) { text_type = 0; }
    var variable_value;
    if (index_type == 0) {
        variable_value = constant;
    }
    else {
        if (text_type == 0) {
            variable_value = "[" + variable + "-" + getTypeDataListName(variable_type, variable) + "]";
        }
        else {
            variable_value = variable + "-" + getTypeDataListName(variable_type, variable);
        }
    }
    return variable_value;
}
